mapper.insert:
insert into SYS_CANDIDATE_INFO (ID, CANDIDATE_NAME, UNIT,BIRTHDAY,DEPARTMENT,CREATION_TIME,POSITION,POSITION_LEVEL,TYPE )
select a."ID" ID,a."NAME" CANDIDATE_NAME,a.COMPANY UNIT,a.BIRTH_DATE BIRTHDAY, a.org DEPARTMENT,sysdate  CREATION_TIME,a.POSITION,a.POSITION_LEVEL,#{type,jdbcType=DECIMAL} from SYS_USER a
where a.ID = #{id,jdbcType=DECIMAL}

mapper.select:
select ID ,CANDIDATE_NAME,UNIT,POSITION ,POSITION_LEVEL,CANDIDATE_TYPE,
ADJUST_TYPE,ASPIRING_UNIT,ASPIRING_POSITION,ASPIRING_POSITION_LEVEL,NOTE from SYS_CANDIDATE_INFO
where ID = #{id,jdbcType=DECIMAL}
